import { useState } from "react";
export default function Board() {
  const [squareValues, setValues] = useState(Array(9).fill(null))
  function handleClick(){
    const nextSquares = squareValues.slice();
    squareValues[i] = "X";
    setSquares(nextSquares);
  }
  return( 
    <>
      <div className="board-row">
        <Square value={squareValues[0]} onSquareClick={()=>handleClick(0)}/>
        <Square value={squareValues[1]} onSquareClick={()=>handleClick(1)}/>
        <Square value={squareValues[2]} onSquareClick={()=>handleClick(0)}/>
      </div>
      <div className="board-row">
        <Square value={squareValues[3]}/>
        <Square value={squareValues[4]}/>
        <Square value={squareValues[5]}/>
      </div>
      <div className="board-row">
        <Square value={squareValues[6]}/>
        <Square value={squareValues[7]}/>
        <Square value={squareValues[8]}/>
      </div>
    </>
  );

}

function Square({value, onSquareClick}){

  return(
    <button className="square" onClick={onSquareClick}>{value}</button>
  );
}
